INSERT INTO `ti_question` VALUES (2060, 0, 'D', NULL, NULL, 106, NULL, '测试用题', '1单选题·3分14．2014年10月31日，中国盐业协会披露，第七套盐业体制改革方案已在国家发改委主任办公会议通过，并在各部委完成意见征求。方案核心为允许食盐流通企业跨区经营，放开盐产品流通经营。此项改革措施体现了现代市场的特征是（  ）A统一性B开放性C竞争性D有序性查看题目解析>', '1单选题·3分14．2014年10月31日，中国盐业协会披露，第七套盐业体制改革方案已在国家发改委主任办公会议通过，并在各部委完成意见征求。方案核心为允许食盐流通企业跨区经营，放开盐产品流通经营。此项改革措施体现了现代市场的特征是（  ）A统一性B开放性C竞争性D有序性查看题目解析>', NULL, 'B', 1, 1, 'swing', '2020-09-13 20:42:57', 'swing', '2020-09-13 20:42:57', '测试用题');
